var express = require('express');
var bodyParser = require('body-parser');
var clienteDAO = require('dao/clienteDAO');


var router = express.Router();
router.use(bodyParser.json());

router.route('/')
	.get(function (req, res, next) {
		clienteDAO.buscarTodos(function(listaClientes){
 			res.setHeader('Content-Type', 'application/json');
      		res.write(JSON.stringify(listaClientes));
      		res.end();
      	});
	})
	.post(function (req, res, next) {
		clienteDAO.insertar(
			req.body,
			function(data){
				res.setHeader('Content-Type', 'application/json');
      			res.write(JSON.stringify(data));
      			res.end();
			}
		);		
	})
	.put(function (req, res, next) {
		clienteDAO.actualizar(
			req.body,
			function(data){
				res.setHeader('Content-Type', 'application/json');
      			res.write(JSON.stringify(data));
      			res.end();
			}
		);
	});

// Setup the item routes
router.route('/:id')
	.get(function (req, res, next) {
	var id = req.params['id'];
	if (id){
		clienteDAO.buscar(
			id,
			function(data){
				res.setHeader('Content-Type', 'application/json');
      			res.write(JSON.stringify(data));
      			res.end();
			}
		);
	}
	else {
		res.send(404, { status: 'Not found' });
	}
}).delete(function (req, res, next) {
		var id = req.params['id'];
		clienteDAO.proyectosAsignadosAlCliente(id,function(cantidadProy){
			if(cantidadProy==0){
				clienteDAO.borrar(id,
					function(id){
						res.sendStatus(204);
					}
				);
			}else{
				res.status(400).send('El cliente no se puede borrar porque tiene  proyectos asignados');
			}
		});
	})
.all(function (req, res, next) {  /// para todos los dem√°s no implementado
	res.send(501, { status: 'Not implemented' });
});

module.exports = router;